// Theme Switching
function changeTheme() {
    const footer = document.querySelector('.main-footer');
    const selectedTheme = document.getElementById('color-theme').value;

    // Remove all themes
    footer.classList.remove('theme1', 'theme2', 'theme3', 'theme4', 'theme5', 'theme6');

    // Apply selected theme
    footer.classList.add(selectedTheme);
}

// Dark Mode Toggle
function toggleDarkMode() {
    document.body.classList.toggle('dark-mode');
    document.querySelector('.main-footer').classList.toggle('dark-mode');

    // Save preference to local storage
    const isDarkMode = document.body.classList.contains('dark-mode');
    localStorage.setItem('darkMode', isDarkMode);
}

// Load Dark Mode Preference on Page Load
window.onload = () => {
    changeTheme();

    // Apply dark mode if saved in local storage
    if (localStorage.getItem('darkMode') === 'true') {
        document.body.classList.add('dark-mode');
        document.querySelector('.main-footer').classList.add('dark-mode');
    }
};

// Newsletter Subscription Function
function subscribe() {
    const emailInput = document.getElementById('email');
    const message = document.getElementById('subscription-message');

    if (emailInput.value.includes('@')) {
        message.textContent = "Thank you for subscribing!";
        message.style.color = "green";
        emailInput.value = ""; // Clear input field
    } else {
        message.textContent = "Please enter a valid email!";
        message.style.color = "red";
    }
}

