/* Animated Button */
.animated-button {
    border-radius: 8px;
    padding: 1.5rem 5rem;
    font-size: 2rem;
    font-weight: bold;
    background-color: yellow;
    color: black;
    border: none;
    cursor: pointer;
    transition: background 0.5s ease-in-out, transform 0.3s;
}

/* Button Hover Effect with 4 Gradient Colors */
.animated-button:hover {
    background: linear-gradient(90deg, red, blue, green, orange);
    color: white;
    transform: scale(1.1);
}

/* Fade-in Animation for Text */
.fade-in {
    opacity: 0;
    animation: fadeInAnimation 2s ease-in-out forwards;
}

@keyframes fadeInAnimation {
    0% { opacity: 0; transform: translateY(-10px); }
    100% { opacity: 1; transform: translateY(0); }
}

